        for (let i = 0; i < horizontalAxis.length; i++) {

            let k = i + j 
            if (k % 2 === 0) {

                board.push(

                    <div className="white-tile">

                        {/* [{horizontalAxis[i]}{verticalAxis[j]}] */}

